"use client"

import * as React from "react"
import { Loader, LoaderCircle, X } from "lucide-react"

import { cn } from "@/lib/utils"

import { ToastClassNames, ToastParams } from "./notify-types"
import {
  progressBarVariants,
  toastActionVariants,
  toastVariants,
} from "./notify-variants"

const ToastTitle = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement> & { text: React.ReactNode }
>(({ text, className, ...props }, ref) => (
  <p
    ref={ref}
    className={cn("mr-auto text-sm font-medium", className)}
    {...props}
  >
    {text}
  </p>
))

const ToastDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement> & {
    description?: ToastParams["description"]
    status: ToastParams["status"]
  }
>(({ description, status, className, ...props }, ref) => {
  if (!description || status === "loading") return null
  return (
    <p
      ref={ref}
      className={cn("text-[0.8125rem] leading-5 opacity-80", className)}
      {...props}
    >
      {description}
    </p>
  )
})

const ToastCloseButton = React.forwardRef<
  HTMLButtonElement,
  React.ButtonHTMLAttributes<HTMLButtonElement> & {
    onClose: () => void
    status: string
  }
>(({ onClose, status, className, ...props }, ref) => {
  if (status === "loading") return null
  return (
    <button
      ref={ref}
      onClick={onClose}
      className={cn(
        "cursor-pointer rounded-sm p-1 opacity-90 ring-offset-background transition-opacity hover:bg-black/5 hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 dark:opacity-70 dark:hover:bg-white/10",
        className
      )}
      {...props}
    >
      <X className="h-4 w-4" />
      <span className="sr-only">Close</span>
    </button>
  )
})

type ToastLoaderProps = {
  status: string
  variant?: ToastParams["loaderVariant"]
  className?: string
}

const ToastLoader = ({
  status,
  variant = "loader-1",
  className,
}: ToastLoaderProps) => {
  if (status !== "loading") return null
  const baseClass = "animate-spin size-4 text-black"

  return variant === "loader-2" ?
      <LoaderCircle className={cn(baseClass, className)} />
    : <Loader className={cn(baseClass, className)} />
}

const ToastProgressBar = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & {
    duration?: number
    status: ToastParams["status"]
    hideProgressBar?: boolean
    paused?: boolean
  }
>(({ duration, status, hideProgressBar, className, paused, ...props }, ref) => {
  if (!duration || status === "loading" || hideProgressBar) return null
  return (
    <div
      ref={ref}
      className={cn(progressBarVariants({ status }), className)}
      style={{
        animation: `shrink ${duration}ms linear forwards`,
        animationPlayState: paused ? "paused" : "running",
      }}
      {...props}
    >
      <style>
        {`
          @keyframes shrink {
            from { width: 0%; }
            to { width: 100%; }
          }
        `}
      </style>
    </div>
  )
})

export function ToastAction({
  label,
  onClick,
  variant,
  status,
}: {
  label: string
  onClick: () => void
  variant: "primary" | "dismiss"
  status: ToastParams["status"]
}) {
  return (
    <button
      onClick={onClick}
      className={cn(
        toastActionVariants({ variant }),
        status === "default" &&
          variant === "primary" &&
          "bg-black text-white hover:bg-black/90 dark:bg-white dark:text-black dark:hover:bg-white/90"
      )}
    >
      {label}
    </button>
  )
}

// Animation variants are now handled directly in the Toast provider

export function Toast({
  closable,
  description,
  duration,
  onClose,
  text,
  title,
  status = "default",
  loaderVariant,
  classNames = {},
  hideProgressBar,
  actions,
  paused,
}: ToastParams & { classNames?: ToastClassNames; paused?: boolean }) {
  return (
    <div
      className={cn(
        toastVariants({ status }),
        classNames[status as keyof ToastClassNames]
      )}
      role={status === "error" ? "alert" : "status"}
    >
      <div className="flex w-full items-center justify-between">
        <ToastLoader
          status={status}
          className={cn("mr-2", classNames.loader)}
          variant={loaderVariant}
        />
        <div className="flex flex-1 flex-col gap-1">
          <ToastTitle text={text ?? title} className={classNames.title} />
          <ToastDescription
            description={description}
            status={status}
            className={classNames.description}
          />
        </div>
        {closable && !actions && (
          <ToastCloseButton
            onClose={onClose!}
            status={status}
            className={cn("ml-2", classNames.closeButton)}
          />
        )}
      </div>
      {actions && (
        <div className="mt-3 flex justify-end gap-2">
          {actions.dismiss && (
            <ToastAction
              label={actions.dismiss.label}
              onClick={actions.dismiss.onClick || onClose!}
              variant="dismiss"
              status={status}
            />
          )}
          <ToastAction
            label={actions.primary.label}
            onClick={actions.primary.onClick}
            variant="primary"
            status={status}
          />
        </div>
      )}
      <ToastProgressBar
        duration={duration}
        status={status}
        hideProgressBar={hideProgressBar}
        className={classNames.progressBar}
        paused={paused}
      />
    </div>
  )
}

ToastTitle.displayName = "ToastTitle"
ToastDescription.displayName = "ToastDescription"
ToastCloseButton.displayName = "ToastCloseButton"
ToastLoader.displayName = "ToastLoader"
ToastProgressBar.displayName = "ToastProgressBar"

"use client"

import { useEffect, useState } from "react"
import { AnimatePresence, motion } from "framer-motion"
import { createPortal } from "react-dom"

import { cn } from "@/lib/utils"

import { Toast } from "./notify"
import { getAnimationProps } from "./notify-animations"
import { DEFAULT_CONFIG } from "./notify-config"
import { useToastStateManager } from "./notify-state-manager"
import type {
  PromiseHandler,
  ToastPosition,
  ToastProviderProps,
} from "./notify-types"
import { toast } from "./notify-utils"
import { toastPositionVariants } from "./notify-variants"

/**
 * Portal component that mounts toast notifications to the document body
 * @param children Content to render within the portal
 */
function ToastPortal({ children }: { children: React.ReactNode }) {
  const [mounted, setMounted] = useState(false)

  useEffect(() => {
    setMounted(true)
  }, [])

  return mounted ? createPortal(children, document.body) : null
}

/**
 * Provider component for toast notifications
 * Must wrap the application to enable toast functionality
 *
 * @param children Child components to render
 * @param position Default position for toasts
 * @param duration Default duration in milliseconds
 * @param classNames Default CSS class names
 * @param closable Whether toasts are closable by default
 * @param preventDuplicates Whether to prevent duplicate toasts
 * @param maxToast Maximum number of toasts to show at once
 * @param hideProgressBar Whether to hide progress bars
 * @param animation Default animation style
 */
export function ToastProvider({
  children,
  position: defaultPosition = DEFAULT_CONFIG.position,
  duration = DEFAULT_CONFIG.duration,
  classNames = {},
  closable = DEFAULT_CONFIG.closable,
  preventDuplicates = DEFAULT_CONFIG.preventDuplicates,
  maxToast = DEFAULT_CONFIG.maxToast,
  hideProgressBar = DEFAULT_CONFIG.hideProgressBar,
  animation = DEFAULT_CONFIG.animation,
}: ToastProviderProps) {
  const {
    toastsByPosition,
    push,
    pushPromise,
    dismiss,
    pauseToast,
    resumeToast,
  } = useToastStateManager({
    position: defaultPosition,
    duration,
    classNames,
    closable,
    preventDuplicates,
    maxToast,
    hideProgressBar,
    animation,
  })

  // Register the toast handlers
  useEffect(() => {
    toast.setHandlers(push, pushPromise as PromiseHandler, dismiss)

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  const toastContainer = (
    <div className="pointer-events-none fixed inset-0 z-[9999]">
      {Object.entries(toastsByPosition).map(([position, positionToasts]) => (
        <div
          key={position}
          className={cn(
            toastPositionVariants({
              position: position as ToastPosition,
            }),
            classNames.containerClassName
          )}
        >
          <AnimatePresence mode="popLayout">
            {positionToasts.map((toast) => {
              const { id, dismiss: dismissToast, params, paused } = toast
              const toastAnimation = params.animation || animation
              const toastPosition = params.position || position

              const animationProps = getAnimationProps(
                toastAnimation,
                toastPosition as ToastPosition
              )

              return (
                <motion.div
                  key={id}
                  layout
                  {...animationProps}
                  className="pointer-events-auto mb-3.5 w-full last:mb-0"
                  style={{
                    zIndex:
                      positionToasts.length - positionToasts.indexOf(toast),
                  }}
                  onMouseEnter={() => pauseToast(id)}
                  onMouseLeave={() => resumeToast(id)}
                  drag
                  dragConstraints={{ left: 0, right: 0, top: 0, bottom: 0 }}
                  dragElastic={0.5}
                  onDragEnd={(_, info) => {
                    const threshold = 0
                    const { x, y } = info.offset
                    if (Math.abs(x) > threshold || Math.abs(y) > threshold) {
                      dismiss(id)
                    }
                  }}
                >
                  <Toast
                    {...params}
                    onClose={() => dismiss(id)}
                    paused={paused}
                  />
                </motion.div>
              )
            })}
          </AnimatePresence>
        </div>
      ))}
    </div>
  )

  return (
    <>
      {children}
      <ToastPortal>{toastContainer}</ToastPortal>
    </>
  )
}

"use client"

import type {
  PromiseHandler,
  ToastFunction,
  ToastMethods,
  ToastParams,
  ToastPromiseOptions,
} from "./notify-types"

/**
 * Simple ID generator for toast notifications
 */
let toastId = 0

/**
 * Generate a unique ID for a toast
 */
export const generateId = () => String(toastId++)

/**
 * Toast API class implementing ToastMethods interface
 * Provides a single point of access for creating and managing toast notifications
 */
class Toast implements ToastMethods {
  private emit: ((params: ToastParams) => string) | null = null
  private emitPromise: PromiseHandler | null = null
  private emitDismiss: ((id: string | number) => void) | null = null

  /**
   * Sets up the handlers for the Toast API
   * Called by ToastProvider on initialization
   */
  setHandlers(
    emit: (params: ToastParams) => string,
    promiseHandler: PromiseHandler,
    dismissHandler: (id: string | number) => void
  ) {
    this.emit = emit
    this.emitPromise = promiseHandler
    this.emitDismiss = dismissHandler
  }

  private createToastFn(status: ToastParams["status"]): ToastFunction {
    return (text, options = {}) => {
      if (!this.emit) {
        throw new Error(
          "Toast not initialized: wrap your app with ToastProvider"
        )
      }
      return this.emit({
        text,
        status,
        ...options,
      })
    }
  }

  /**
   * Display a success toast notification
   * @param text - The main message to display
   * @param options - Optional configuration for the toast
   * @example
   * toast.success('Profile updated successfully')
   * toast.success('Files uploaded', { duration: 5000, description: '3 files uploaded' })
   */
  success = this.createToastFn("success")

  /**
   * Display an error toast notification
   * @param text - The error message to display
   * @param options - Optional configuration for the toast
   * @example
   * toast.error('Failed to save changes')
   * toast.error('Upload failed', { description: 'Network error occurred' })
   */
  error = this.createToastFn("error")

  /**
   * Display a warning toast notification
   * @param text - The warning message to display
   * @param options - Optional configuration for the toast
   * @example
   * toast.warning('Low storage space')
   * toast.warning('Session expiring', { description: 'Please save your work' })
   */
  warning = this.createToastFn("warning")

  /**
   * Display an info toast notification
   * @param text - The information message to display
   * @param options - Optional configuration for the toast
   * @example
   * toast.info('New updates available')
   * toast.info('Tips', { description: 'Swipe left to delete' })
   */
  info = this.createToastFn("info")

  /**
   * Display a loading toast notification
   * @param text - The loading message to display
   * @param options - Optional configuration for the toast
   * @example
   * toast.loading('Uploading files...')
   * toast.loading('Processing', { duration: Infinity })
   */
  loading = this.createToastFn("loading")

  /**
   * Display a default toast notification
   * @param text - The message to display
   * @param options - Optional configuration for the toast
   * @example
   * toast.default('Something happened')
   */
  default = this.createToastFn("default")

  /**
   * Dismiss a toast notification by its ID
   * @param id - The ID of the toast to dismiss
   * @example
   * const toastId = toast.info('Loading...');
   * toast.dismiss(toastId);
   */
  dismiss = (id: string | number) => {
    if (this.emitDismiss) {
      this.emitDismiss(id)
    }
  }

  /**
   * Create a custom toast notification with full control over its properties
   * @param params - Complete toast parameters
   * @example
   * toast.push({
   *   title: 'Custom Toast',
   *   status: 'info',
   *   duration: 3000,
   *   description: 'This is a custom toast',
   *   position: 'top-right'
   *   preventDuplicates: true,
   * })
   */
  push = (params: ToastParams) => {
    if (!this.emit)
      throw new Error("Toast not initialized: wrap your app with ToastProvider")
    return this.emit(params)
  }

  /**
   * Handle async operations with loading, success, and error states
   * @param promise - Function that returns a promise
   * @param options - Configuration for different states of the promise
   * @example
   * toast.promise(
   *   () => fetchUserData(),
   *   {
   *     loading: 'Fetching user...',
   *     success: (data) => `Welcome ${data.name}!`,
   *     error: 'Failed to fetch user'
   *   }
   * )
   */
  promise = <T>(promise: () => Promise<T>, options: ToastPromiseOptions<T>) => {
    if (!this.emitPromise)
      throw new Error("Toast not initialized: wrap your app with ToastProvider")
    return this.emitPromise(promise, options)
  }
}

/**
 * Exported toast instance for use throughout the application
 */
export const toast = new Toast()

"use client"

import { useCallback, useEffect, useRef, useState } from "react"

import type {
  AnimationType,
  ToastParams,
  ToastPromiseOptions,
  ToastProviderProps,
  ToastState,
  ToastTimeoutRef,
} from "./notify-types"
import { generateId } from "./notify-utils"

/**
 * Custom hook for managing toast state and notifications
 * Provides functions for creating, updating, and dismissing toast notifications
 *
 * @param config Configuration options for toast state management
 * @returns Toast state manager with functions for toast operations
 */
export function useToastStateManager({
  position: defaultPosition = "bottom-right",
  duration: defaultDuration = 4000,
  classNames: defaultClassNames = {},
  closable: defaultClosable = true,
  preventDuplicates: defaultPreventDuplicates = false,
  maxToast: defaultMaxToast = 4,
  hideProgressBar: defaultHideProgressBar = false,
  animation: defaultAnimation = "slide",
}: Omit<ToastProviderProps, "children">) {
  const [toasts, setToasts] = useState<ToastState[]>([])
  const toastsRef = useRef<Record<string, ToastTimeoutRef>>({})

  // Toast dismissal function
  const dismiss = useCallback((id: string | number) => {
    setToasts((prev) => prev.filter((t) => t.id !== String(id)))
    const toast = toastsRef.current[String(id)]
    if (toast) {
      clearTimeout(toast.timeout)
      if (toast.onClose) {
        toast.onClose()
      }
      delete toastsRef.current[String(id)]
    }
  }, [])

  // Create a function to generate a dismiss handler for a specific toast ID
  const createDismiss = useCallback(
    (id: string) => () => dismiss(id),
    [dismiss]
  )

  // Push a new toast notification
  const push = useCallback(
    (params: ToastParams) => {
      const id = params.id ?? generateId()
      const toastDuration = params.duration ?? defaultDuration

      const dismissToast = createDismiss(id)

      // Update the toast state using a functional update to prevent stale state
      setToasts((prevToasts) => {
        // Handle duplicate prevention
        if (params.preventDuplicates ?? defaultPreventDuplicates) {
          const isDuplicate = prevToasts.some(
            (toast) =>
              toast.params.text === params.text &&
              toast.params.status === params.status
          )
          if (isDuplicate) {
            return prevToasts
          }
        }

        const filteredToasts = prevToasts.filter((t) => t.id !== id)
        const newToast: ToastState = {
          dismiss: dismissToast,
          id,
          params: {
            ...params,
            duration: toastDuration,
            closable: params.closable ?? defaultClosable,
            hideProgressBar: params.hideProgressBar ?? defaultHideProgressBar,
            classNames: params.classNames ?? defaultClassNames,
            position: params.position ?? defaultPosition,
            animation: (params.animation ?? defaultAnimation) as AnimationType,
          },
          paused: false,
        }

        const updatedToasts = [newToast, ...filteredToasts]
        return updatedToasts.slice(0, params.maxToast ?? defaultMaxToast)
      })

      // Clear any existing timeout for this toast ID
      if (toastsRef.current[id]) {
        clearTimeout(toastsRef.current[id].timeout)
      }

      // Set a new timeout if duration is finite
      if (toastDuration !== Infinity) {
        toastsRef.current[id] = {
          timeout: setTimeout(dismissToast, toastDuration),
          remaining: toastDuration,
          startTime: Date.now(),
          onClose: params.onClose,
        }
      }

      return id
    },
    [
      defaultPreventDuplicates,
      defaultDuration,
      defaultClosable,
      defaultHideProgressBar,
      defaultClassNames,
      defaultPosition,
      defaultMaxToast,
      defaultAnimation,
      createDismiss,
    ]
  )

  // Handle promise-based toasts
  const pushPromise = useCallback(
    <T>(promise: () => Promise<T>, options: ToastPromiseOptions<T>) => {
      const id = generateId()
      const dismiss = createDismiss(id)
      const position = options.position
      const toastDuration = options.duration ?? defaultDuration
      const toastClassNames = options.classNames ?? defaultClassNames
      const toastAnimation = options.animation ?? defaultAnimation

      // Show loading toast
      push({
        status: "loading",
        text: options.loading,
        id,
        position,
        classNames: toastClassNames,
        duration: toastDuration,
        animation: toastAnimation,
      })

      // Handle promise resolution and rejection
      promise()
        .then((data) => {
          // Clear existing timeout
          if (toastsRef.current[id]) {
            clearTimeout(toastsRef.current[id].timeout)
          }

          // Update with success state
          setToasts((prev) => [
            {
              dismiss,
              id,
              params: {
                status: "success",
                text: options.success(data),
                duration: toastDuration,
                closable: true,
                classNames: toastClassNames,
                position,
                hideProgressBar: defaultHideProgressBar,
                animation: toastAnimation,
              },
              paused: false,
            },
            ...prev.filter((t) => t.id !== id),
          ])

          // Set new timeout
          if (toastDuration !== Infinity) {
            toastsRef.current[id] = {
              timeout: setTimeout(dismiss, toastDuration),
              remaining: toastDuration,
              startTime: Date.now(),
            }
          }
        })
        .catch(() => {
          // Clear existing timeout
          if (toastsRef.current[id]) {
            clearTimeout(toastsRef.current[id].timeout)
          }

          // Update with error state
          setToasts((prev) => [
            {
              dismiss,
              id,
              params: {
                status: "error",
                text: options.error ?? "Error",
                duration: toastDuration,
                classNames: toastClassNames,
                position,
                hideProgressBar: defaultHideProgressBar,
                animation: toastAnimation,
              },
              paused: false,
            },
            ...prev.filter((t) => t.id !== id),
          ])

          // Set new timeout
          if (toastDuration !== Infinity) {
            toastsRef.current[id] = {
              timeout: setTimeout(dismiss, toastDuration),
              remaining: toastDuration,
              startTime: Date.now(),
            }
          }
        })

      return id
    },
    [
      defaultDuration,
      defaultClassNames,
      defaultAnimation,
      defaultHideProgressBar,
      push,
      createDismiss,
    ]
  )

  // Pause a toast (e.g., on mouse hover)
  const pauseToast = useCallback((id: string) => {
    if (toastsRef.current[id]) {
      clearTimeout(toastsRef.current[id].timeout)
      const remaining =
        toastsRef.current[id].remaining -
        (Date.now() - toastsRef.current[id].startTime)
      toastsRef.current[id].remaining = remaining
      setToasts((prev) =>
        prev.map((t) => (t.id === id ? { ...t, paused: true } : t))
      )
    }
  }, [])

  // Resume a toast
  const resumeToast = useCallback(
    (id: string) => {
      if (toastsRef.current[id]) {
        toastsRef.current[id].startTime = Date.now()
        toastsRef.current[id].timeout = setTimeout(
          () => dismiss(id),
          toastsRef.current[id].remaining
        )
        setToasts((prev) =>
          prev.map((t) => (t.id === id ? { ...t, paused: false } : t))
        )
      }
    },
    [dismiss]
  )

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      Object.values(toastsRef.current).forEach((toast) =>
        clearTimeout(toast.timeout)
      )
      toastsRef.current = {}
    }
  }, [])

  // Organize toasts by position for efficient rendering
  const toastsByPosition = (() => {
    const positions = {} as Record<string, ToastState[]>
    for (const toast of toasts) {
      const position = toast.params.position ?? defaultPosition
      positions[position!] = positions[position!] || []
      positions[position!].push(toast)
    }
    return positions
  })()

  return {
    toasts,
    toastsByPosition,
    push,
    pushPromise,
    dismiss,
    pauseToast,
    resumeToast,
  }
}

"use client"

import { cva } from "class-variance-authority"

/**
 * Toast positioning variants for different screen positions
 * Defines positioning classes for each toast location option
 */
export const toastPositionVariants = cva(
  "absolute w-full max-w-[420px] p-4 md:p-8",
  {
    variants: {
      position: {
        "top-left": "left-0 top-0 md:top-0",
        "top-center": "left-1/2 top-0 -translate-x-1/2 transform md:top-0",
        "top-right": "right-0 top-0 md:top-0",
        "bottom-left": "bottom-0 left-0 md:bottom-0",
        "bottom-center":
          "bottom-0 left-1/2 -translate-x-1/2 transform md:bottom-0",
        "bottom-right": "bottom-0 right-0 md:bottom-0",
      },
    },
    defaultVariants: {
      position: "bottom-right",
    },
  }
)

/**
 * Toast type variants for different notification types
 * Defines style classes for success, error, warning, etc.
 */
export const toastTypeVariants = cva("toast-base", {
  variants: {
    type: {
      success: "toast-success",
      error: "toast-error",
      warning: "toast-warning",
      info: "toast-info",
      loading: "toast-loading",
      default: "toast-default",
    },
  },
  defaultVariants: {
    type: "default",
  },
})

/**
 * Status styles for different notification types
 * Defines style classes for success, error, warning, etc.
 */
export const statusStyles = {
  error:
    "dark:bg-[#24161b] bg-[#fff5f5] text-red-900 dark:text-[#ffdfdd] dark:border-red-900 border-red-200",
  warning:
    "dark:bg-[#1E1A1B] bg-[#fefae1] text-[#3b2212] dark:text-[#EADB90] dark:border-[#5C431B] border-[#ddcab8]",
  success:
    "dark:bg-[#131d1e] bg-[#e7fef6] text-[#0d311e] dark:text-[#abf9de] dark:border-[#1e5643] border-green-200",
  info: "dark:bg-[#161831] bg-[#edf4ff] text-[#1e3a8a] dark:text-[#DCE6FF] dark:border-[#2f3873] pborder-[#bfdbfe]",
  default:
    "dark:bg-[#13141b] bg-white text-gray-900 dark:text-[#e4e5e9] dark:border-[#3a3c4a] border-gray-200",
  loading: "bg-white text-gray-900 border-gray-200  ",
} as const

/**
 * Toast variants for different notification types
 * Defines style classes for success, error, warning, etc.
 */
export const toastVariants = cva(
  "relative flex w-full flex-col gap-1 overflow-hidden rounded-lg border p-[0.75rem] shadow-lg",
  {
    variants: {
