"use client";

import { Card } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import useInViewState from "@/hooks/use-inview-state";
import { motion } from "framer-motion";

function Section({ title, index }: { title: string; index: number }) {
  const { ref, isInView } = useInViewState({ threshold: 0.5 });

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
      animate={isInView ? { opacity: 1, x: 0 } : {}}
      transition={{ duration: 0.5 }}
      className="min-h-[200px]"
    >
      <Card
        className={`p-6 ${
          isInView ? "bg-gradient-to-r from-primary/10 to-primary/5" : ""
        } transition-colors duration-500`}
      >
        <div className="flex items-center gap-4">
          <div
            className={`
            w-12 h-12 rounded-full flex items-center justify-center
            ${isInView ? "bg-primary text-primary-foreground" : "bg-muted"}
            transition-colors duration-500
          `}
          >
            {index + 1}
          </div>
          <div className="space-y-2">
            <h3 className="text-xl font-semibold">{title}</h3>
            <p className="text-muted-foreground">
              {isInView
                ? "You're reading this section! ðŸ‘€"
                : "Scroll to reveal this section"}
            </p>
          </div>
        </div>
      </Card>
    </motion.div>
  );
}

export default function InViewStateScrollDemo() {
  const sections = [
    "Introduction",
    "Getting Started",
    "Key Features",
    "Advanced Usage",
    "Best Practices",
    "Conclusion",
  ];

  return (
    <Card className="border-2">
      <ScrollArea className="h-[400px] p-4">
        <div className="space-y-6 p-2">
          {sections.map((title, index) => (
            <Section key={title} title={title} index={index} />
          ))}
        </div>
      </ScrollArea>
    </Card>
  );
}
