"use client";

import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import useScreenSize from "@/hooks/use-screen-size";
import { Monitor, Smartphone, Tablet, Tv } from "lucide-react";
import { useState } from "react";

export default function ScreenSizeDemo() {
  const { screenSize, isMobile } = useScreenSize();
  const [forcedWidth, setForcedWidth] = useState<string | null>(null);

  const breakpoints = [
    {
      name: "Mobile",
      width: "400px",
      icon: <Smartphone className="w-5 h-5" />,
      expected: ["xs", "sm"],
      color: "from-pink-500 to-rose-500",
    },
    {
      name: "Tablet",
      width: "758px",
      icon: <Tablet className="w-5 h-5" />,
      expected: ["md"],
      color: "from-blue-500 to-cyan-500",
    },
    {
      name: "Desktop",
      width: "1200px",
      icon: <Monitor className="w-5 h-5" />,
      expected: ["lg", "2xl"],
      color: "from-green-500 to-emerald-500",
    },
    {
      name: "4K",
      width: "1800px",
      icon: <Tv className="w-5 h-5" />,
      expected: ["4k"],
      color: "from-purple-500 to-violet-500",
    },
  ];

  const activeBreakpoint = breakpoints.find((point) =>
    point.expected.includes(screenSize || "")
  );

  return (
    <div className="space-y-6">
      <Card className="p-6 relative overflow-hidden">
        {/* Gradient Background */}
        <div
          className={`absolute inset-0 opacity-5 bg-gradient-to-r transition-all duration-500 ${
            activeBreakpoint?.color || "from-gray-500 to-slate-500"
          }`}
        />

        {/* Content */}
        <div className="relative space-y-6">
          <div className="flex gap-3 flex-wrap">
            {breakpoints.map((point) => (
              <Button
                key={point.name}
                variant={forcedWidth === point.width ? "default" : "outline"}
                onClick={() =>
                  setForcedWidth(
                    forcedWidth === point.width ? null : point.width
                  )
                }
                className={`group relative overflow-hidden transition-all ${
                  point.expected.includes(screenSize || "")
                    ? "ring-2 ring-offset-2 ring-offset-background"
                    : ""
                }`}
              >
                <div
                  className={`absolute inset-0 opacity-10 bg-gradient-to-r ${point.color} 
                    transition-opacity group-hover:opacity-20`}
                />
                <div className="relative flex items-center gap-2">
                  {point.icon}
                  <span>{point.name}</span>
                </div>
              </Button>
            ))}
          </div>

          <div className="space-y-4">
            {/* Preview Area */}
            <div
              style={{ width: forcedWidth || "100%" }}
              className="h-32 rounded-lg bg-muted/30 border-2 border-dashed 
                border-muted-foreground/20 flex items-center justify-center
                transition-all duration-300 relative group"
            ></div>
            <div
              className="absolute inset-0 bg-gradient-to-r opacity-5
                transition-all duration-500 group-hover:opacity-10
                ${activeBreakpoint?.color || 'from-gray-500 to-slate-500'}"
            />
            <div className="flex items-center gap-3 text-muted-foreground">
              {activeBreakpoint?.icon}
              <span className="font-medium">
                {activeBreakpoint?.name || "Unknown"} View
              </span>
            </div>
          </div>

          {/* Info Section */}
          <div className="grid gap-4 sm:grid-cols-2">
            <div className="rounded-md bg-muted/50 p-3">
              <p className="text-sm font-medium mb-1">Screen Size</p>
              <div className="flex items-center gap-2">
                {activeBreakpoint?.icon}
                <span className="font-mono">{screenSize || "unknown"}</span>
              </div>
            </div>

            <div className="rounded-md bg-muted/50 p-3">
              <p className="text-sm font-medium mb-1">Device Type</p>
              <div className="flex items-center gap-2">
                {isMobile ? (
                  <Smartphone className="w-4 h-4" />
                ) : (
                  <Monitor className="w-4 h-4" />
                )}
                <span className="font-mono">
                  {isMobile ? "Mobile" : "Desktop"}
                </span>
              </div>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}
