"use client";

import useFileUpload from "@/hooks/use-file-upload";
import { useState } from "react";
import { SingleImageDropzone } from "../reusables/single-image-upload";

export const FileUploadDemo = () => {
  // For auto-upload demo
  const autoUpload = useFileUpload({
    onSuccess: (url) => console.log("Auto Upload Success:", url),
    onError: (error) => console.error("Auto Upload Error:", error),
  });

  // For manual upload demo
  const manualUpload = useFileUpload({
    onSuccess: (url) => console.log("Manual Upload Success:", url),
    onError: (error) => console.error("Manual Upload Error:", error),
  });
  const [manualFile, setManualFile] = useState<File | undefined>();

  return (
    <div className="mx-auto max-w-2xl space-y-8 p-6">
      {/* Auto Upload Demo */}
      <div className="space-y-4">
        <h2 className="text-xl font-bold">Direct Upload (Automatic)</h2>
        <SingleImageDropzone
          width={"600px"}
          height={"300px"}
          directUpload={true}
          progress={autoUpload.progress}
          disabled={autoUpload.isUploading}
          onChange={async (file) => {
            if (file) {
              try {
                await autoUpload.handleFileUpload(file);
              } catch (error) {
                console.error("Upload error:", error);
              }
            }
          }}
        />
        <div className="space-y-2">
          {autoUpload.error && (
            <p className="text-red-500">Error: {autoUpload.error}</p>
          )}
          {autoUpload.data && (
            <div>
              <p className="text-green-600">Upload successful!</p>
              <p className="text-sm text-gray-600 break-all">
                URL: {autoUpload.data}
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Manual Upload Demo */}
      <div className="space-y-4">
        <h2 className="text-xl font-bold">Manual Upload</h2>
        <SingleImageDropzone
          width={"600px"}
          height={"300px"}
          directUpload={false}
          progress={manualUpload.progress}
          disabled={manualUpload.isUploading}
          onChange={(file) => setManualFile(file)}
        />
        <div className="space-y-2">
          <button
            onClick={() =>
              manualFile && manualUpload.handleFileUpload(manualFile)
            }
            disabled={!manualFile || manualUpload.isUploading}
            className="rounded bg-blue-600 px-4 py-2 text-white disabled:bg-gray-400 hover:bg-blue-700"
          >
            {manualUpload.isUploading ? "Uploading..." : "Start Upload"}
          </button>
          {manualUpload.error && (
            <p className="text-red-500">Error: {manualUpload.error}</p>
          )}
          {manualUpload.data && (
            <div>
              <p className="text-green-600">Upload successful!</p>
              <p className="text-sm text-gray-600 break-all">
                URL: {manualUpload.data}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default FileUploadDemo;
